(function(){


  var showPopOver = function() {
        var action = prismpop.style.display === "none" ? 'block' : 'none';
        prismpop.style.display = action;
    };
     
    var hidePopOver = function() {
        prismpop.style.display = "none";
    };
     
    var prism = document.createElement("div");
    prism.id = "prismbutton";
    // The prism logo in base64
    prism.innerHTML = "<a id='showprismpop'><img src='data:image/png;base64,'></a>";
    
    var prismpop = document.createElement("div");
    prismpop.id = "prismpop";
    prismpop.style.display = "none";
    prismpop.innerHTML = "The revelations about the National Security Agency's surveillance apparatus, if true, represent a stunning abuse of our basic rights. We demand the U.S. Congress reveal the full extent of the NSA's spying programs. Sign the petition now: <a target='_blank' style='color:red; font-weight:bold; text-decoration:underline;' href='https://optin.stopwatching.us/'>StopWatching.Us</a>. <div id='grayprism'>If you want this icon in your website use this <a href='http://jsbin.com/evafaz/4/edit'>code snippet.</a> <br><br><a id='hideprism'><span id='prismclose'>CLOSE</span></a></div>";
     
    document.body.appendChild(prism); 
    document.body.appendChild(prismpop); 

    var undef;
    var btnShow = document.getElementById('showprismpop');
    var btnHide = document.getElementById('hideprism');
    var IE = !!btnShow.attachEvent;
    var method = IE ? 'attachEvent' : 'addEventListener';
    var type = IE ? 'onclick' : 'click';

    btnShow[method](type, showPopOver, IE ? undef : true );
    btnHide[method](type, hidePopOver, IE ? undef : true );

     
    var css = '/*prismpop.css*/ ' +
    '#prismpop{ ' +
        'padding: 20px; ' +
        'width: 100px; ' +
        'height: auto; ' +
        'background: #eaeaea; ' +
        'position: absolute; ' +
        'display: none; ' +
        'border-color: #444141; ' +
        'border-style: solid; ' +
        'border-width: 1px; ' +
        'width: 300px; ' +
        'z-index: 2; ' +
        '-moz-border-radius: 15px; ' +
        '-webkit-border-radius: 15px; ' +
        'border-radius: 15px; ' +
        'position:absolute; ' +
        'right:110px; ' +
        'top:10px; ' +
        'font-size: 14px;' +
        'color: black' + 
    '} ' +
    '#prismbutton{ ' +
        'width: 100px; ' +
        'height: 100px; ' +
        'background: none; ' +
        'color: white; ' +
        'position: absolute; ' +
        'right: 0px !important; ' +
        'top: 0px !important; ' +
        'cursor: pointer;' +
    '} ' +
    '#prismclose{ ' +
        '-moz-border-radius: 5px; ' +
        '-webkit-border-radius: 5px; ' +
        'background-color:  red;' +
        'border-radius: 5px; ' +
        'border:1px solid #666666; ' +
        'padding:3px; ' +
        'float:right; ' +
        'cursor: pointer;' +
        'color: white;' +
        'font-weight: bold;' +
        'font-size: 14px;' +
    '}' +
    '#grayprism {' +
        'color:#999;' +
        'font-size:14px;' +
        'margin-top:10px;' +
    '}';

    var head = document.getElementsByTagName('head')[0];
    var style = document.createElement('style');

    style.type = 'text/css';
    if (style.styleSheet){
      style.styleSheet.cssText = css;
    } else {
      style.appendChild(document.createTextNode(css));
    }

    head.appendChild(style);

})();